00250 @Override
00251 public void nextMove(ParserOutput p, Window window) {
00252     parserOutput = p;
00253     messages.clear();
00254 
00255     if (p.getCommand() == null) {
00256         window.showMessage("Non ho capito cosa devo fare! Prova con un altro comando.");
00257     } else {
00258         Room cr = getCurrentRoom();
00259         notifyObservers(window);
00260         boolean move = !cr.equals(getCurrentRoom()) && getCurrentRoom() != null;
00261 
00262         // Controllo per il comando LOOK_AT
00263         if (p.getCommand().getType() == CommandType.LOOK_AT) {
00264             AdvObject object = p.getObject();
00265             if (object != null) {
00266                 window.showMessage("Osservi " + object.getName() + ": " + object.getDescription());
00267             } else {
00268                 window.showMessage(cr.getLook());
00269                 if (cr.getName().equalsIgnoreCase("Anticamera")) {
00270                     if (cr.isMonsterAlive()) {
00271                         cr.setDynamicLook("Sei nell'anticamera, l'alieno gigante si sta svegliando e ti osserva con occhi minacciosi!\n");
00272                     }
00273                     window.showMessage(cr.getDynamicLook());
00274                 }
00275             }
00276         } else {
00277             if (!messages.isEmpty()) {
00278                 for (String m : messages) {
00279                     if (m.length() > 0) {
00280                         window.showMessage(m);
00281                     }
00282                 }
00283             }
00284 
00285             if (move) {
00286                 Room currentRoom = getCurrentRoom();
00287                 if (currentRoom.getName().equals("Sala motori")) {
00288                     boolean hasKey = getInventory().stream().anyMatch(obj -> obj.getId() == 5);
00289                     if (hasKey) {
00290                         if (isKeyUsed()) {
00291                             window.showRoomName(currentRoom.getName());
00292                             window.showRoomDescription(currentRoom.getDescription());
00293                         } else {
00294                             window.showMessage("La porta è chiusa ma hai la chiave. Usa la chiave per aprirla.");
00295                             setCurrentRoom(cr);
00296                         }
00297                     } else {
00298                         window.showMessage("La porta è chiusa e non puoi entrare perché non hai la chiave.");
00299                         setCurrentRoom(cr); // Torna alla stanza precedente
00300                     }
00301                 } else {
00302                     window.showRoomName(currentRoom.getName());
00303                     window.showRoomDescription(currentRoom.getDescription());
00304                 }
00305             } else {
00306                 // Se non c'è stato un movimento, mostra la stanza corrente
00307                 window.showRoomName(cr.getName());
00308                 window.showRoomDescription(cr.getDescription());
00309             }
00310         }
00311    }
00312 }
