00325     @Override
00326     public void attach(GameObserver o) {
00327         if (!observer.contains(o)) {
00328             observer.add(o);
00329         }
00330     }
00331 
00337     @Override
00338     public void detach(GameObserver o) {
00339         observer.remove(o);
00340     }
00341 
00348     @Override
00349     public void notifyObservers(Window window) {
00350         for (GameObserver o : observer) {
00351             messages.add(o.update(this, parserOutput, window));
00352         }
00353     }
