00317         exitAndSaveButton.addActionListener((ActionEvent e) -> {
00318             while (true) {
00319                 String gameName = JOptionPane.showInputDialog("Inserisci il nome della partita da salvare:");
00320                 if (gameName == null) {
00321                     JOptionPane.showMessageDialog(null, "Operazione di salvataggio annullata.");
00322                     break; // Esci dal ciclo se l'utente preme "Cancel"
00323                 } else if (!gameName.trim().isEmpty()) {
00324                     if (!SaveGame.gameExists(gameName.trim())) {
00325                         boolean monsterAlive=true, isDoorOpen;
00326                         List<Room> rooms;
00327                         rooms=game.getRooms();
00328                         for (Room room : rooms) {
00329                             if (room.getId()==8) {
00330                                 monsterAlive=room.isMonsterAlive();
00331                             }
00332                         }
00333                         isDoorOpen = game.isKeyUsed();
00334                         SaveGame.save(game.getCurrentRoom(), game.getInventory(), gameName.trim(), elapsedSeconds, monsterAlive, isDoorOpen);
00335                         JOptionPane.showMessageDialog(null, "Partita salvata con successo!");
00336                         sottofondo.stopSound();
00337                         System.exit(0);
00338                         break; // Esci dal ciclo dopo un salvataggio riuscito
00339                     } else {
00340                         JOptionPane.showMessageDialog(null, "Una partita con questo nome esiste gi. Inserisci un altro nome.");
00341                     }
00342                 } else {
00343                     JOptionPane.showMessageDialog(null, "Nome della partita non valido! Inserisci un nome valido.");
00344                 }
00345             }
00346         });
